css:
@-moz-document url-prefix("http://www.akipress.org/"), url-prefix("http://akipress.org/") {
body * {font-family: Calibri, sans-serif}

div.body-stretch > table:last-child {display:none}

div.body-stretch > table > tbody > tr:nth-child(1) {display:none}

div.body-stretch div.crossprj2 + div {display:none}

div.body-stretch div.crossprj2 + div + table > tbody > tr > td:first-child {display:none}

div.body-stretch div.crossprj2 + div + table > tbody > tr > td:last-child {display:none}

div.auto_block {display:none}

table#maintbl {background: none;}

table#last50 {width: 100%}

table#last50 > tbody > tr > td {padding-top:5px; padding-bottom:5px}

table#last50 > tbody > tr:nth-child(odd) {background: #f3f3f3}
table#last50 > tbody > tr:hover > td {border-top:1px solid #000; border-bottom:1px solid #000}
div#next50 + table > tbody > tr:nth-child(9) > td > table > tbody > tr:nth-child(odd) {background: #f3f3f3}
div#next50 + table > tbody > tr:nth-child(9) > td > table > tbody > tr:hover > td {border-top:1px solid #000; border-bottom:1px solid #000}
div#next50 + table > tbody > tr:nth-child(9) > td > table > tbody > tr > td {padding-top:5px; padding-bottom:5px}

div[id^=akicon] {display:none}

img[alt='Twitter'] {display:none}

img[alt='В контакте'] {display:none}

img[alt='Мой мир'] {display:none}

img[alt='facebook'] {display:none}

div#text_rights {display:none}
}
