css:
@-moz-document url-prefix("http://tazabek.kg/"), url-prefix("http://www.tazabek.kg/") {
table.lenta {margin-bottom: 50px}
div.crosstop {display:block}
tr#header {display:block}
table#main > tbody > tr:nth-child(4) {display:none}
tr#footer {display:none}
td.lblock {display:none}
td.rblock {display:none}

div.auto_block {display:none}

table.mainnews > tbody > tr > td.ctext > table {display:none}


tr.cstring > td:nth-child(2) {width: 100%}

tr.cstring > td:nth-child(2) > div {display:none}

tr.tmenu > td.nowdate {display:none}

div.discus-notauth {display:none}
div.discus-notauth + div {display:none}

body * {font-family: Calibri, sans-serif;}
table.lenta > tbody > tr:nth-child(odd) {background-color: #f6f6f6}

div[id^=akicona] {display:none}

img[alt='Twitter'] {display:none}

img[alt='В контакте'] {display:none}

img[alt='Мой мир'] {display:none}

img[alt='facebook'] {display:none}

div#text_rights {display:none}
}
