css:
@-moz-document url-prefix("http://kg.akipress.org/"), url-prefix("http://www.kg.akipress.org/") {
body {background: none !important}
body * {font-family: Calibri, sans-serif}

div.crosstop {display:block}

div.maintbl {display:none}

table.maintbl > tbody > tr:first-child  {display:none}

table.maintbl > tbody > tr:last-child  {display:none}

table.maintbl td.maintblr {display:none}

table.mainbody > tbody > tr > td.mainbodyl:first-child {display:none}

table.mainbody > tbody > tr > td.mainbodyr:first-child {display:none}

div.crossprj2 {display:none}

table.mainbody {background: none}

div.blocknews > table > tbody > tr:nth-child(odd) {background: #f6f6f6}

.blocknews td {border-top:none}

div[id^=akicona] {display:none}

div[id$=carousel] {display:none}
div[id^=carousel] {display:none}

div[id^=akicona] {display:none}

img[alt='Twitter'] {display:none}

img[alt='В контакте'] {display:none}

img[alt='Мой мир'] {display:none}

img[alt='facebook'] {display:none}

div#text_rights {display:none}
}
