css:
@-moz-document domain("zhihu.com") {
body, div:not(.zu-top)
{ background-color: #222 !important; }
/*背景色*/

.zu-top
{ top: -10px !important; transition: all 1s ease-in-out !important; opacity: 0 !important; }

.zu-top:hover
{ top: -1px !important; transition: all 1s ease-in-out !important; opacity: 1 !important; }

div, span.zm-favo-list-item-title
{ color: #ddd !important; }

a
{ color: #608dff !important; text-decoration: none; }

a:hover
{ color: #637ab5 !important; text-decoration: underline; }
}
