css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("springloops.io") {
    
    /* Using default system font*/
    body,
    textarea,
    input[type="text"],
    input[type="email"],
    input[type="password"],
    select {
        font: inherit;
        background: #222;
        color: #eee;
    }
    
    .event-details .event-title {
        color: inherit;
        font-size: inherit;
    }
    
    h2 a ,
    .project-tile h2 a {
        color: inherit;
    }
    
    a,
    .table._tasks td.ticket-title a {
        color: #393;
        text-decoration: underline;
    }
    
    a:hover {
        color: green;
    }
    
    .BigBen {
        background: inherit;
    }
    
    /* Narrow tables to save space */
    table.table th,
    table.table td {
        line-height: inherit;
    }
    .table._tasks td {
        padding: 0;
    }
    
    /* Simple Flat "buttons" */
    .btn-wrap .btn {
        background-image: none;
        box-shadow: none;
        border: 1px solid rgba(255, 255, 255, 0.2);
        background: rgba(0, 0, 0, 0.3);
        border-radius: 0;
    }
    
    .btn-wrap .btn:hover {
        background: rgba(0, 0, 0, 0.6);
        border: 1px solid rgba(255, 255, 255, 0.2);
        color: inherit;
    }
    
    .btn-wrap .btn > em, .btn-wrap .btn span > em {
        color: inherit;
    }
    
    input[type="text"],
    input[type="email"],
    input[type="password"],
    textarea,
    select {
        transition: none;
    }
    
    .nav-dropdown {
        transition: none;
    }
    
    .right-wrap, #sidebar {
        transition: none;
    }
    
    /* Narrow top "all projects" link */
    .BigBen {
        top: 0px;
    }
    .BigBen .go-home {
        left: 0px;
        right: 0px;
        top: -10px;
        height: 10px;
    }
    .BigBen .go-home > a {
        height: 10px;
        padding: 0;
        line-height: 100%;
        color: inherit;
        box-shadow: none;
        border: none;
        background: black;
        color: white;
        font-size: 9px;
        text-align: center;
    }
    .BigBen .go-home > a > strong {
        display: none;
    }
    .BigBen._project {
        top: 10px;
        right: 0;
        left: 32px;
    }
    
    .BigBen._project #sideBar {
        top: -10px;
        left: -32px;
        width: 32px;
    }
    
    .BigBen #sideBar {
        overflow: hidden;
    }
    
    .BigBen #sideBar:hover {
        overflow: visible;
    }
    
    .BigBen > header {
        height: 20px;
        overflow: hidden;
        background : #383A3C;
    }
    
    .links-box p {
       border-color: rgba(255, 255, 255, 0.2)    ;
    }
    
    .project-tile h2 {
       border-color: rgba(255, 255, 255, 0.2)    ;
    }
    
    .project-tile .users-wrap > button {
        background: rgba(0, 0, 0, 0.3);
        border-color: rgba(0, 0, 0, 1);
    }
    
    .BigBen > header:hover {
        height: auto;
        background : white;
        overflow: visible;
    }
    
    /* Hidint project colour-coded strip */
    .springloops .BigBen > header .account-name,
    .springloops .BigBen > header nav{
        box-shadow: none;
    }
    
    .servicesBox {
        display: none;
    }
    
    .right-wrap,
    .left-wrap {
        top: 20px;
    }
    
    .left-wrap .leftContentWrap {
        padding: 0;
        max-height: none !important;
    }
    
    #sideBar .sidebar-elements > ul li:before {
        display: none;
    }
    
    #sideBar .sidebar-elements,
    #sideBar .sidebar-elements > ul,
    #sideBar .sidebar-elements > ul li {
        width: auto !important;
        font-size: inherit;
        overflow: visible;
        background: #383A3C;
    }
    
    #sideBar .sidebar-elements > ul li:hover {
        background: black;
    }
    
    /* No max width */
    .window-size {
        width: auto;
    }
    
    
    .left-wrap,
    .right-wrap {
        background: transparent;
    }
    
    body.fullScreen .BigBen._project #sideBar {
        top: 0;
    }
    
    #sideBar .sidebar-elements > ul li {
        transition: none;
    }
    
    /* Dropdown menus */
    /*.BigBen > header > nav > ul > li:hover > ul:not(.search-dropdown) {*/
    .BigBen > header > nav > ul > li > ul:not(.search-dropdown) {
        display: block;
    }
    
    .BigBen > header > nav > ul > li > ul {
        /*width: 130px;*/
        min-width: 0;
        max-width: none;
        padding: 0;
        box-shadow: none;
        background: #222;
        
    }
   
    .BigBen > header > nav > ul > li > ul:hover a._hover, .BigBen > header > nav > ul > li > ul:hover a:hover {
        background-color: black;
    }
    
    .BigBen > header > nav > ul > li > ul:hover a._hover, .BigBen > header > nav > ul > li > ul:hover a._hover:hover {
        background-color: black;
    }

    .project-tile > p {
        display: none;
    }
    
    .project-tile h2 {
        height: auto;
       line-height: 100%;
    }
    
    .project-tile > a._acs {
        background: none;
    }
    
    .project-tile > a._acs:before {
        content: 'GET SOURCE URL';
    }
    
    .project-tile > span, .project-tile > a._acs {
    height: auto;
    width: 100%;
        border-radius: 0;
    }
    
    .project-tile {
        height: 160px;
        width: 168px;
        transition: none;
    }
    
    .project-tile > em {
        display: none;
    }
    
    .project-tile .users-wrap {
        background: transparent;
    }
    
    .table._tasks {
        background: transparent;
    }
    
    ._zebra tbody > tr:nth-child(2n+1) > td, ._zebra tbody > tr:nth-child(2n) > th {
        background: rgba(255, 255, 255, 0.05);
    }
    
    .prv_pn {
        background: transparent;
    }
    
    table.table td {
        border-bottom: 1px solid rgba(255, 255, 255, 0.2);
    }
    
    .event-preview {
        background: #222222;
    }
    
    .sidebar-wrap .close-sidebar {
        background: darkred;
        height: 100%;
        width: 16px;
        right: -16px;
        top: 0px;
    }
    
    .sidebar-wrap .close-sidebar:before {
        content: 'CLOSE';
        background: darkred;
    }
    
    .sidebar-wrap .close-sidebar:hover {
        background: red;
    }
    
    
    .sidebar-wrap .close-sidebar:hover:before {
        background: red;
    }
    
    .sidebar-wrap { 
        background: #222222;
    }
    
    .jQ_tickerMask {
        background: black;
    }
    
    .project-tile {
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    
    div.project-tile > h2 > a {
        color: #aaa;
        padding: 3px;
        text-decoration: none;
    }
    div.project-tile > h2 {
        background: rgba(255, 255, 255, 0.1);
    }

    
    .project-tile > span._str:hover, .project-tile > a._acs:hover {
       background: transparent;
    }
    
    .jQ_scrollMe {
        overflow: visible;
        overflow-x: hidden;
        height: auto !important;
    }
    
    .jQ_rowColumns {
        transition: none;
    }
    
    #ticketDesk .right-wrap {
        overflow: scroll;
    }
    
    .table._tasks td.ticket-title a {
        font-size: 14px;
    }
    
    
    body.external.login {
        background: #111;
    }
    
    body.external .register-wrap, body.external .login-wrap {
       background: #222;
        border: none;
    }
    .external-top-info h2 {
        color: inherit;
    }
    
    .project-tile:hover {
        border-color: rgba(255, 255, 255, 0.4) !important;
    }
    
    .log-wrap input[type="password"]:focus,
    .log-wrap input[type="email"]:focus,
    .log-wrap input[type="text"]:focus,
    .existing-log-wrap input[type="password"]:focus,
    .existing-log-wrap input[type="email"]:focus,
    .existing-log-wrap input[type="text"]:focus {
        background: #222;
    }
    
    body.external .login-wrap .external-app-logo {
        display: none;
    }
    
    body.external.springloops .login-wrap:before {
        display: none;
    }
    
    .project-tile .users-wrap {
        padding: 0;
        height: auto;
    }
    
    .BigBen > header > nav,
    .springloops .BigBen > header .account-name {
        background: #333;
    }
    
    .BigBen > header .account-name .name {
        color: #bbb;
    }
    
    .BigBen > header > nav > ul > li:hover > a {
        color: #bbb;
    }
    
    .project-tile .users-wrap > a {
        margin: 0;
                display: inline-block;
        float: none;
    }
    
    .project-tile .users-wrap .in-touch {
        margin: 0;
        
        display: inline-block;
        float: none;
    }
    
    .project-tile .stack-a {
        display: none;
    }
    
    .project-tile .users-wrap {
        text-align: center;
    }
    
    .BigBen > header > nav > ul a:hover {
        color: white;
    }
    
    .BigBen > header > nav > ul > li > ul a {
        color: #bbb;
    }
    
    .activity-update {
        background: rgba(255, 255, 255, 0.05);
        border: 1px solid rgba(255, 255, 255, 0.2);
    }
    
    .tx_tl {
        color: inherit;
    }
    
    .statement-box .s-header .user {
        color: inherit;
    }
    
    .s-wrap-blank {
        background: rgba(255, 255, 255, 0.05);
        border: 1px solid rgba(255, 255, 255, 0.2);
    }
    
    .activity-update:before {
        border-bottom-color: rgba(255, 255, 255, 0.2);
        top: -7px;
    }
    
    .links-box > div {
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .stacked-wrap header > span {
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .links-box p:last-child {
        border-bottom: none;
    }
    
    .links-box > div:last-child {
       border-bottom: none;
    }
    
    .in-touch > span {
        opacity: 0.7;
    }
    
    .description-event {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .description-event:before {
        border-bottom-color: rgba(255, 255, 255, 0.2);
        top: -17px;
    }
    
    .bC {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .deploy-wrap .bC .autDy {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .switch-wrap._sml {
        background: rgba(0, 0, 0, 0.2);
    }
    
    .actionBar {
        background: rgba(255, 255, 255, 0.05);
        border: none;
    }
    
    .prv_pn {
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .actionBar > .btn-oce:hover {
        background: rgba(255, 255, 255, 0.05);
    }

    .actionBar > .btn-oce {
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .ticket-card {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.2);
    }
    .ticket-card .info-action {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.2);
    }
    
    .ticket-simple {
        background: rgba(255, 255, 255, 0.05);
        border-color: rgba(255, 255, 255, 0.3);
    }
    
    .ticket-simple.low {
        border-color: rgba(255, 255, 255, 0.1);
    }
    
    .ticket-simple.minor {
        border-color: rgba(255, 255, 255, 0);
    }
    
    .ticket-simple > p {
       background: #333;
    }
    
    .ticket-simple .t-title {
        color: #bbb !important;
    }
    
    .sts {
        background: rgba(0, 0, 0, 0.5);
        border-color: rgba(255, 255, 255, 0.2);
        box-shadow: none;
    }
    
    .ticket-simple.act_prv,
    .ticket-simple.selTic,
    .ticket-card.act_prv,
    .ticket-card.selTic {
        background: rgba(0, 0, 0, 0.4);
    }
    
    .ticket-simple.act_prv > p, .ticket-simple.selTic > p,     
    .ticket-card.act_prv > p, .ticket-card.selTic > p {
        background: #111;
    }
    
    .sidebar-wrap .project-list:hover {
        background-color: rgba(255, 255, 255, 0.1);
    }
    
    .ui-datepicker {
        background-color: rgba(255, 255, 255, 0.1);
    }
    
    .int1 a { background-color:rgba(160, 234, 190, 0.2); }
    .int2 a { background-color:rgba(250, 219, 98, 0.2); }
    .int3 a { background-color:rgba(251, 179, 55, 0.2); }
    .int4 a { background-color:rgba(255, 116, 58, 0.2); }
    .int5 a { background-color:rgba(254, 73, 67, 0.2); }
    .int6 a { background-color:rgba(170, 14, 0, 0.2); }
    
    .ui-datepicker .ui-datepicker-header .ui-datepicker-title {
        color: inherit;
    }
    
    .btn-wrap .btn._main, .btn-wrap .btn._action,
    .btn-wrap .btn._mega {
        background: #777;
        color: #FFF;
        text-shadow: none;
        border: 1px solid #aaa;
    }
    
    .stackedBoxes-wrap {
        background: none;
    }
    
    .new-ticket-blank {
        background: none;
        border: none;
    }
    
    .cloud-notice.jQ_msg {
        display: none;
    }
}
