Body {
	background:url("http://i59.tinypic.com/wpu36.png") no-repeat fixed !important;
	background-color: rgba(0,0,0,1.00) important;
	background-position: 0% 0% !important;
  	background-size: cover !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(0,0,0,0) !important;
  }

#logo {
	background:url("http://i62.tinypic.com/2nbx2bk.png") no-repeat !important;
	height: 0 !important;
  	width: 0 !important;
  	padding-top: 85px !important;
  	padding-left: 350px !important;
}


#posts .post .post_content {
  background: transparent !important;
}


#posts .post {
  background: rgba(255,255,255,1) !important;
