css:
@-moz-document url-prefix("http://www.newsru.co.il/") {
HTML>BODY>TABLE:nth-of-type(4)
{
  width: 100%;
  /*float: left;*/
}

HTML>BODY>TABLE:nth-of-type(4)>tbody>tr>td:nth-of-type(3)
{
  width: 100%;
  padding-right: 300px;
}
div#stakan2
{
  right:0px !important;
  left: initial !important;
}
div#stakan2 td 
{
  font-size: 19px;
  line-height: initial;
}
div#stakan2 td a
{
  font-size: 19px;
  line-height: initial;
}

.maintext, .headcolumn, .explaincolumn
{
  font-size: 21px;
  line-height: initial;
}

HTML>BODY>TABLE:nth-of-type(7)
{
  width: 100%;
  padding-right: 300px;
}
}
