css:
@-moz-document url-prefix("http://stackoverflow.com/questions/") {
#header,#herobox-mini,#sidebar,#post-form,.bottom-notice,#footer,#feed-link,.newsletter-anon,.fw,#question > table > tbody > tr:nth-child(2),#tabs,.topbar, .vote>a {
	display: none;
}

#answers {
clear: none;
padding-top: 10px;
width: 100%;
}

#content, #answers, .answer,#mainbar, .mainbar, table, .post-text, .question, .comments,#question-header,#answers-header {
	width: 100% !important;
	float: none !important;
	clear: none !important;
}

#question-header h1 {
font-size: 2em;
text-align: center;
}

.post-text {
	font-size: 1.3em !important;
}

.comment-copy {
	font-size: 1.2em;
}

.subheader h1, .subheader h2 {
	float: none !important;
	text-align: center !important;
}
}
