css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.opennet.ru") {
  body   {font-family: sans !important;}
  #r_title, #r_memo, .tnews {
    font-size: 18px !important;
    line-height: 22px !important;
  }
  .ctxt *  {font-size: 18px !important;}
  .tab, .tab-selected {font-size: 12px !important;} /* переключатели*/
  body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {width: 100% !important;}
  body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {width: 0 !important; display: none;}
}
