css:
@-moz-document domain("anarchy101.org") {

    .qa-search {margin-bottom: -25px}
    .qa-nav-user {margin-top: -9px}

    body, .qa-body-wrapper
    {
        background: rgb(25,25,25)!important;
        color: rgb(70,70,70)!important;
    }
    
    .qa-nav-main-link{background: rgb(40,40,40)}
    .qa-nav-main-link:hover {background: rgb(50,50,50)}
    .qa-nav-main-link:active, .qa-nav-main-selected {background: rgb(60,60,60)}
    .qa-a-selected-text {margin-top: -10px}
    
    .qa-voting, .qa-sidebar, .qa-a-count, .qa-top-tags-label, .qa-a-item-selected, .qa-notice
    {
        background: rgb(30,30,30)!important;
    }
    
    .qa-logo, .qa-footer, .qa-a-selected {display: none;}
}
