css:
@-moz-document url-prefix(http://www.webdeveloper.com/forum/) {
  div#heightcontainer, div#navitoolbarcontainer, div#navitoolbarcontainer + script + img, div#navitoolbarcontainer + script + img + br, div#navitoolbarcontainer + script + img + br + a + table tr td:first-child a + br, div#navitoolbarcontainer + script + img + br + a + table tr td:first-child a + br ~ *, div#navitoolbarcontainer + script + img + br + a + table tr td:last-child, div#navitoolbarcontainer + script + img + span, div#navitoolbarcontainer + script + img + span + br + a + table tr td:first-child a + br, div#navitoolbarcontainer + script + img + span + br + a + table tr td:first-child a + br ~ *, div#navitoolbarcontainer + script + img + span + br + a + table tr td:last-child, table[style*="margin-top: -15px"] + br + a + table tr td:first-child a + br, table[style*="margin-top:-15px"] + br + a + table tr td:first-child a + br, table[style*="margin-top: -15px"] + br + a + table tr td:first-child a + br ~ *, table[style*="margin-top:-15px"] + br + a + table tr td:first-child a + br ~ *, table[style*="margin-top: -15px"] + br + a + table tr td:last-child, table[style*="margin-top:-15px"] + br + a + table tr td:last-child, table#main > tbody > tr > td:last-child, center#bot, center#bot + center, center#bot + center + div, table.page + br {
    display: none !important;
  }

  table[style*="margin-top: -15px"], table[style*="margin-top:-15px"], table[style*="margin-top: -15px"] tr td, table[style*="margin-top:-15px"] tr td {
    color: #fff !important;
    background: #600 !important;
    border: 0 !important;
    margin: 0 !important;
  }

  table[style*="margin-top: -15px"] tr td a:link, table[style*="margin-top:-15px"] tr td a:link, table[style*="margin-top: -15px"] tr td a:visited, table[style*="margin-top:-15px"] tr td a:visited {
    color: #fff !important;
  }

  table[style*="margin-top: -15px"] tr td a:hover, table[style*="margin-top:-15px"] tr td a:hover, table[style*="margin-top: -15px"] tr td a:active, table[style*="margin-top:-15px"] tr td a:active {
    color: #ff6 !important;
  }

  div#navitoolbarcontainer + script + img + br + a + table tr td:first-child a, div#navitoolbarcontainer + script + img + span + br + a + table tr td:first-child a, table[style*="margin-top: -15px"] + br + a + table tr td:first-child a, table[style*="margin-top:-15px"] + br + a + table tr td:first-child a {
    margin: 10px 0 0 10px !important;
  }

  table#main {
    width: 100% !important;
  }
}
