css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("read.qidian.com") {
    #qd_reader_header .page_set .gg_box {
        width: auto;
    }
    #bookreader .booktitle {
        padding: 0;
    }
    #bookreader .bookcontent {
        line-height: normal;
        padding: 0;
    }
    #bookreader .bookcontent p {
        margin: 0;
    }
    #content > p:last-of-type {
        display: none;
    }
    .bottom_opt {
        margin: 0;
    }
    .all_tj {
        display: none;
    }
    #readerfooter {
        display: none;
    }
}
