css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www12.atwiki.jp/xbox360score), url-prefix(http://www56.atwiki.jp/xboxonescore) {

body { background-color: #eee !important; color: #333 !important; }
#container a:link { color: #228 !important; text-decoration: none !important; }
#container a:hover { background-color: #ddf }
#container a:visited { color: #62a !important; text-decoration: none !important; }

#header { margin-bottom: 0 !important; border: none !important; }
#header p { display: none !important; }
#menubar { margin: 0 !important; width: 240px !important; font-size: 14px !important; }
#menubar > br { display: none !important; }
#menubar hr { border-color: #888; }
#menubar hr:nth-child(6), #left hr:nth-child(49), #left hr:nth-child(65) { display: none !important; }
#menubar div:nth-child(1), #left div:nth-child(2), #left div:nth-child(4) { display: none !important; }
#menubar .plugin_treemenu { margin: 4px 0 !important; }

#menubar2 .plugin_recent, #menubar2 .plugin_popular { font-size: 12px !important; }
#menubar2 .plugin_recent_ul, #menubar2 .plugin_popular ul { margin: 0 !important; }

#contents { margin-left: 250px !important; border: 2px solid #666 !important; font-size: 14px !important; }
#contents #wikibody { color: #333 !important; }
#wikibody h2, #wikibody h3, #wikibody h3, #wikibody h4 { color: #000 !important; }
#contents #wikibody > div:first-child { font-size: 20px !important; }
#contents #wikibody table { margin-left: 30px !important; }
#contents #wikibody table td { font-size: 13px !important; }
#contents #wikibody table tr:nth-child(2n) { background-color: #ddd; }
#contents #wikibody table tr td:nth-child(3) { text-align: right; }
#contents #wikibody img[src$="file=tit.jpg"] { border-radius: 35px; }

} /*end-moz-doc*/
