css:
@-moz-document domain("douyu.com"), domain("douyutv.com") {
/*tr*/

#header > div > div > div.o-login.fl > div > div > div.box2.skill-level,
#header > div > div > div.o-login.fl > div > div > div.box1.chat-member,
#header > div > div > div.o-login.fl > div > div > div.box5.task.clearfix,
/*u*/

#header > div > ul > li.fl.assort,
#header > div > ul > li.fl.funny,
#header > div > ul > li.fl.yuba,
/*ul*/

#anchor-info > div.relate-text.fl > div.headline.clearfix > a,
/*r*/

#js-live-room-recommend,
#js-chat-notice,
/*ru*/

#js-fans-rank,
/*ur*/

#anchor-info > div.btn-group.fr > div.sq-wrap,
/*rd*/

#js-live-room-normal-equal-right > div.column.rec,
/*d*/

#js-live-room-normal-equal-left,
/*dl*/

#js-stats-and-actions > div.task-Getyw.clearfix.fl > div.task-btn.fl.bor,
#js-yw-rmd-rooms,
#js-stats-and-actions > div.task-Getyw.clearfix.fl > div.get-yw.fl > div.yw-collect-box.ywrmd > p,
/*dr*/

#gift-content
{ display: none !important;
	}

#header
{ top: -20px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 0 !important;
	}

#header:hover
{ top: 0px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 1 !important;
	}

#left
{ left: -10px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 0 !important;
	}

#left:hover
{ left: 0px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 1 !important;
	}

#anchor-info,
#js-live-room-normal-right > div.chat > div.chat-member,
#js-chat-cont,
#js-chat-speak,
#js-stats-and-actions,
#js-live-room-normal-right,
#container,
#mainbody,
#mainbody>div,
#mainbody>div>div,
#mainbody>div>div>div,
#mainbody>div>div>div>div,
#mainbody>div>div>div>div>div,
#mainbody>div>div>div>div>div>div,
#mainbody>div>div>div>div>div>div>div,
#mainbody>div>div>div>div>div>div>div>div,
#mainbody>div>div>div>div>div>div>div>div>div,
#mainbody>div>div>div>div>div>div>div>div>div>div
{ background: transparent!important;
	}

*
{ border-width: 0px !important;
	}

*
{ border-style: none!important;
	}

html,
body
{ background-color: #ff0 !important;
	}

#js-live-room-normal-equal-right > div.column.announce > div.column-body
{ right: -20px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 0 !important;
	}

#js-live-room-normal-equal-right > div.column.announce > div.column-body:hover
{ right: 0px !important;
	transition: all 0.6s ease-in-out !important;
	opacity: 1 !important;
	}

::-webkit-scrollbar
{ width: 2px!important;
	height: 2px!important;
	border: 2px solid #000000!important;
	}

::-webkit-scrollbar-track
{ background: #FD9!important;
	}

::-webkit-scrollbar-thumb
{ background: #000!important;
	-webkit-border-radius: 8px;
	border-radius: 8px!important;
	}
}
