css:
@-moz-document domain("www.bushcraft-deutschland.de"), domain("bushcraft-deutschland.de") {
.messageText, .wbbBoardDescription, .box32, .box48, .content, .dropdownMenu, .boxHeadline, .invisible {
   font-family: sans-serif !important;
   color: #000000;
   text-shadow: none !important;
}
  
h1,h2,h3,h4,h5,h6,h7 {
   font-family: sans-serif !important;
   color: #000000;
   text-shadow: none !important;
}

input, dt, dd, time, legend{
   font-family: sans-serif !important;
   color: #000000 !important;
   text-shadow: none !important;
}

a {
   font-family: sans-serif !important;
   text-shadow: none !important;
}

 
a:link {
   text-shadow: none !important;
   color: #303030 !important;
}

a:visited {
   text-shadow: none !important;
   color: #404040 !important;
}

a:hover {
   color: #505050 !important;
   text-decoration: underline;
   text-shadow: none !important;
}

a:active {
   color: #707070 !important;
   text-shadow: none !important;
}
}
