css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("forum.xda-developers.com"){

@-moz-document url("http://forum.xda-developers.com/donations.php"){

/* MikeChannon Mixed Theme Original Classic / 2015 */

}
#sidepanel {
  display: none;
}
.page {
  
  margin: 0px 5px 18px 5px !important;
}
/* Forum Introduction */
#device_info_wrapper {
	display: none;
}  
  
a:link,body_alink{color:#0000C0;text-decoration:none  ;}
.page{background:#FFFFF1;color:#000;}
.page a:visited,.page_avisited{color:#0000C0;}
.tborder{background:#FFC;color:#000;border:1px solid #0B198C;}
.tcat a:link,.tcat_alink{color:#0000C0;text-decoration:none;}
.tcat a:visited,.tcat_avisited{color:#0000C0;text-decoration:none;}
.tcat a:hover,.tcat a:active,.tcat_ahover{color:#633;text-decoration:none;}
.thead a:visited,.thead_avisited{color:#FFFFF1;}
.thead a:hover,.thead a:active,.thead_ahover{color:#FFC;}
.tfoot a:link,.tfoot_alink{color:#FFFFF1;}
.tfoot a:visited,.tfoot_avisited{color:#FFFFF1;}
.tfoot a:hover,.tfoot a:active,.tfoot_ahover{color:#FFC;text-decoration:underline;}
.alt1,.alt1Active{background:#FFC;color:#000;font-size:11pt;}
.alt2,.alt2Active{background:#FF9;color:#000;}
.alt3{background-color:#FFFFF1;}

/* Headers User CP view */
.thead
{
	background: #A9A9A9 !important;
}
    #header-welcome-user {
    position: relative;
    top: 0px;
    right: 5px;
    color: #FF0000
}
    TD, TH, P, LI {
    font: 18px "Hind",sans-serif;
}
.header-right > LI {
    display: inline-block;
    margin: 0px;
    padding: 0px;
}
    .page {
    margin: 0px 335px 18px 20px;
    display: block;
    background: #EBEBEB !important;
}
A, DIV, P, TD, LI, SPAN {
    max-height: 999999em;
}
#top_tabControls {
    clear: left;
    background-color: #FF9;
    height: 45px;
    width: 100%;}
    
#forumdevice_view {
    clear: left;
    float: left;
    width: 100%;
    margin-top: 15px;
    background-color: #FF9;
}
    
.tabcontrols A.active {
    box-shadow: 0px -3px 0px #FFBD2F inset;
    color: #FF0000;
}
    .tabcontrols A:hover {
    box-shadow: 0px -3px 0px #FFBD2F inset;
    color: #FF0000;
}

/* Thread info column */    

    .forum-head {
    background: none repeat scroll 0% 0% #FF9;
    color: #454545;
} 
.threadbit-announcement {
    clear: left;
    background-color: #FFC;
}
.thread-info-cell {
    background-color: #FFC;
}
.thread-icon-cell {
    background-color: #FFC;
}
        
.thread-info-cell {
    width: 100%;
}
    .thread-row:hover > DIV {
    background-color: #FF9;
}
    
    .thread-row > DIV {
    display: table-cell;
    vertical-align: top;
    border-bottom: 0px solid #D8D8D8;
    padding: 10px 5px 0px;
    
}
    
    .forumbox-row:hover  > DIV {
    background-color: #FF9;
}
    
.count-cell {
    min-width: 100px;
    font-size: 12px;
    background-color: #FFC;  
} 
.alt1, .alt1Active, .alt2, .alt2Active {
    min-width: 100px;
   
   
    padding: 15px 20px;
}

.mod-cell {
    padding: 10px 20px 10px 0px;
    width: 15px;
    background-color: #FFC;
    
}

    .info-cell {
    width: 30% !important;
    min-width: 280px;
    padding: 15px 0px 0px 10px;
    font-size: 12px;
    background-color: #FF9;

}
    
    .showthread-abovebar {
     background-color: #FF9;
}
/* Private Messaging */

.wysiwyg{background:#FFC;color:#000;font:10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;margin:5px 10px 10px 10px;padding:0;}
textarea,.bginput{background:#FFC;font:10pt verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
.bginput option,.bginput optgroup{font-size:10pt;font-family:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
.button{background:#FFFFF1;color:#000;font:11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
.smallfont{font:11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
.time{color:#000;}
.navbar{color:#0000C0;font:11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
.highlight{font-weight:bold;}
.fjsel{background:#FFC;color:#000;}
.fjdpth0{background:#FFFFF1;color:#000;}
.panel{background:#FFFFF1;color:#000;padding:10px;border:2px outset;}
.panelsurround{background:#FFC;color:#000;}
legend{background:transparent;color:#0000C0;font:11px tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;}
    
.tcat {
    background-color: #FF9 !important;
}


    .pmlist-timegroup {
    background-color: #FF9 !important;
    color: #454545 !important;
}
.pmlist-message {
    border-bottom: 0px solid #D8D8D8;
    background-color: #FFC !important;
}
/* Posts */
    #posts .page {
    margin-bottom: 13px;
    /*+box-shadow:0 3px 3px rgba(0, 0, 0, 0.03);*/
  
    -webkit-box-shadow: 0 3px 3px rgba(0, 0, 0, 0.03);
    -o-box-shadow: 0 3px 3px rgba(0, 0, 0, 0.03);
    box-shadow: 0 3px 3px rgba(0, 0, 0, 0.03);
}

.postbit-wrapper {
    /*+box-shadow:0 3px 1px 0 rgba(0, 0, 0, 0.02);*/

    -webkit-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
    -o-box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
    box-shadow: 0 3px 1px 0 rgba(0, 0, 0, 0.02);
    margin-bottom: 15px;
}

.postbit-anchor {
    visibility: hidden;
    position: relative;
    top: -75px;
    display: block;
}

.post-head {
    background: #FF9 ;
    line-height: 46px !important;
    color: #A9A9A9;
    font: 15px 'Hind', sans-serif;
    position: relative;
}

.post-head-container {
    background-color: #FF9 ;
    float: left;
    width: 100%;
    border-top: 1px solid #D8D8D8;
    border-bottom: 1px solid #D8D8D8;
    /*+box-shadow:-1px 3px 3px rgba(0, 0, 0, 0.03);*/
 
    -webkit-box-shadow: -1px 3px 3px rgba(0, 0, 0, 0.03);
    -o-box-shadow: -1px 3px 3px rgba(0, 0, 0, 0.03);
    box-shadow: -1px 3px 3px rgba(0, 0, 0, 0.03);
}

.post-head-left {  
    width: 173px;
    padding-left: 10px !important;
    float: left;
    border-right: 1px solid #D8D8D8;
    border-left: 1px solid #D8D8D8;
}

.post-head-right {
    /*float: right;*/
    border-right: 1px solid #D8D8D8;
    font-size: 14px;
    margin-left: 195px;
}

.postbit-signature {
    border-top: 1px solid #A9A9A9;
    margin: 5px 0 0;
    padding: 5px 0 0;
    max-height: 200px;
    overflow: auto;
}

.postbit-edited {
    border-top: 1px solid #D8D8D8;
    margin: 5px 0 0;
    padding: 5px 0 0;
    font-style: italic;
}

.posterinfo {
    border: 1px solid #D8D8D8;
}

.postbit-userinfo-cell {
    background-color: #FF9 ;
    width: 173px;
    border-left: 1px solid #D8D8D8;
    padding: 5px;
    display: table-cell;
}

.postbit-userinfo-cell .xda-assist-tag {
    background-color: #FFC ;
    display: block;
    width: 70px;
    text-align: center;
    margin: 0 auto 10px;
}

.post-content-container {
    background-color: #FFC ;
    border-bottom: 1px solid #D8D8D8;
    width: 100%;
    position: relative;
    display: table;
    clear: left;
}

.postbit-userinfo-cell:after, .post-content:after {
    display: block;
    position: absolute;
    height: 100%;
    content: "";
    background-color: #000;
}

.postbit-post {
    width: 100%;
    border: 1px solid #FF0000;
}

.postbit-content {
    margin-left: 184px;
    padding: 10px;
    border-left: 1px solid #D8D8D8;
    border-right: 1px solid #D8D8D8;
    height: 100%;
    display: table-cell;
    vertical-align: top;
}

.postbit-content IMG, .post-text IMG {
    max-width: 100%;
}

.postbit-posttitle {
    margin: 10px 0 15px;
}

.thanksShow {
    cursor: pointer;
    background-color: #F9F9F9;
    border: 1px solid #D8D8D8;
    border-top: none;
    font-size: 10px;
    line-height: 18px;
    display: block;
    padding: 5px;
    color: #727272 !important;
}

.thanksButtonThanked {
    background-color: #FFBD2D !important;
}

.thanksNames {
    background-color: #F9F9F9;
    border: 1px solid #D8D8D8;
    border-top: none;
    padding: 10px 5px;
    font-size: 12px;
}

.thanksExpandPhrase {
    float: right;
}

.post-thanks-box-date {
    font-size: 10px;
    margin-left: 3px;
}

.postbit-social {
    float: right;
}

.postbit-social A {
    line-height: 46px;
    height: 46px;
    text-align: center;
    width: 50px;
    margin: 0;
    border-left: 1px solid #D8D8D8;
    font-size: 22px;
    display: inline-block;
    float: right;
}

.postbit-social I {
    line-height: 46px;
    width: 100%;
    text-align: center;}

/*HEADER / NAV Rules*/
#header {
    background: #2E2E2E ;
    padding: 0;
    height: 60px;
    display: block;
    width: 100%;
    /* min-width: 520px; */
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1000;
    border-bottom: 1px solid #D8D8D8;
    /*+box-shadow:0 0 15px 2px rgba(0, 0, 0, 0.3);*/
    
    -webkit-box-shadow: 0 0 15px 2px rgba(0, 0, 0, 0.3);
    -o-box-shadow: 0 0 15px 2px rgba(0, 0, 0, 0.3);
    box-shadow: 0 0 15px 2px rgba(0, 0, 0, 0.3);
}
#navigation {
    margin-top: 60px;
    height: 60px;
    color: #727272;
    background-color: #FFC;
    border-bottom: 1px solid #D8D8D8;
    position: relative;
    
}
#xda-nav {
    background: #FFC;
    height: 60px;
    margin: 0;
    padding: 0;
    position: absolute;
    left: 0;
    top: 0;
    
}    
#himg LI A {
    background: #FFC;
    display: inline-block;
    float: right;
    width: 110px;
    height: 60px;
    text-align: center;
    border-left: 1px solid #D8D8D8;
    vertical-align: middle;
    
} 
#himg LI A IMG {
    background: #FFC;
    margin: 12px 0;
    
}

#social-links {
    background: #FFC;
    margin: 0;
    padding: 0;
    width: 220px;
    height: 61px;
    float: right;
    
}

#social-links LI {
    float: right;
    list-style: none;
}

#social-links LI A {
    color: #A9A9A9;
    line-height: 61px;
    height: 60px;
    display: block;
    text-align: center;
    width: 54px;
    border-left: 1px solid #D8D8D8;
    border-bottom: 1px solid #D8D8D8;
}
    
#xda-nav LI A:hover, #xda-nav-forum {
    color: #FF0000 !important;
    box-shadow: 0px -3px 0px #FFBD2D inset;
    transition: all 0.15s ease-in-out 0s;

}

    .bbcode-quote-text {
    border: 1px solid #D8D8D8;
    background-color: #FFFFF1 ;
    padding: 5px;
    line-height: 133%;
}
    .bbcode-quote-text P {
    line-height: 18px;
}
TD, TH, P, LI {
    font: 14px "Hind",sans-serif;
}
    #privatemessages .fa-envelope {
    margin-right: 2px;
}

.notification-count {
    color: #FF0000 !important;
    background: #FFC !important;
    font-size: 16px; 
    margin: 0 2px;
    padding: 1px 6px !important;
}

#notifications_menu {
    z-index: 999999 !important;
    position: fixed !important;
    top: inherit !important;
    margin-top: 48px !important;
}

#notifications_menu .thead {
    padding: 3px 5px;
}
#footer {
    background: none repeat scroll 0% 0% #454545;
    color: #000000;
    font-size: 12px;
    padding: 0px 0px 15px;
    position: relative;
    float: left;
    clear: both;
    width: 100%;
    margin: 0px;
    background-color: #A7955E ;
}
    #footer-copyright {
    color: #000000;
    font-size: 11px;
    font-style: italic;
}
    .f-links {
    color: #FFBD2D;
    font-size: 20px;
}
  .w-social {
    color: #FFBD2D;
    font-size: 14px;
}  .w-social {
    color: #FFBD2D;
    font-size: 20px;
}
     
    #tabControl_loading, #tabControl_loading:hover {
    border: none !important;
    box-shadow: none !important;
    background-color: #ff9 !important;
}
}
