css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bitsoup.me") {

    a[href*="paypal"], td.navigation a[href*="paypal"], .smallfont {
       display: none;
    }

    a[href="/friends.php"] {
        display: none;
    }

    a[href^="/messages.php"] {
        display: none;
    }

    center {
        display: none;
    }

    img[src^="/banners/"] {
        display: none;
    }

    table.main {
        width: 100%;
    }

    table[width="100%"] {
        margin: auto;
        width: 90% ;
    }

    table[width="600"], table[bgcolor="red"] {
        display: none;
    }

    table[width="750"], table.koptekst {
        width: 90%;
    }

    td.navigation a {
        display: inline-block;
        padding: 5px;
    }

    td.navigation a[href="/getrss.php"], td.navigation a[href="/irc.php"] {
        display: none;
    }

    table.statusbar1 td.main {
        padding: 0 0 10px!important;

    }

    form[action="/browse.php"] td.bottom:nth-child(2) {
        display: none;
    }
    form[action="/browse.php"] table table td.bottom:nth-child(2) {
        display: inline-block;
    }
  
    table.statusbar1 td.main br {
        display: none;
    }
    
    table.koptekst tr td:nth-child(4),
    table.koptekst tr td:nth-child(7),
    table.koptekst tr td:nth-child(13)
        {
        display: none;
    }

}
