css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gw2wvw.org") {
  
  * {
    text-shadow: none !important;
    text-decoration: none !important;
  }

	
  html, body, .headerContainer, .footerContainer {
    background: #222222;
    color: #cccccc;
    font: 11px/13px "Ubuntu",sans-serif,arial;
    border: 0px solid #111;
  }
  
  .mapContainer {

  }
  
  #rankings table, .objInfoArrow, .objInfo {
    background: #262626;
    margin-top: 3px;
    border: 1px solid #303030;
    border-radius: 5px;
  }
  
  .map, .maplayer, .layer, .footer, .header,
  #rankings {
    background: #222222;
    color: #cccccc;
    border: 0px solid #000;
  }
  
  a {
    color: #aaa;
  }
  
  
	
  #map,
  .shadow {
    box-shadow: 0px 0px 10px 10px #222222 !important;
    border-left: 0px solid #222222;
    border-right: 0px solid #222222;
    border-radius: 5px;
	background-color: #222 !important;
  }
}
