css:
@-moz-document domain("8chan.co") {

html, body {
    background-color:#e9edf0 !important;
    background-image:url(http://img405.imageshack.us/img405/1714/fondo1j.jpg) !important;
    background-position: right bottom !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-size: 60%;
}

.button {
    background-color: rgba(255,255,255,0.8) !important;
    border: 1px solid #B7C5D9 !important;
    border-radius: 7px !important;
    padding: 10px !important;
    /*Posts text font below*/
    color: #000000 !important;
}
    
.reply, .replyhl, .catalog-thread, table, .why, #twitter-widget-0 {
    background-color: rgba(255,255,255,0.8) !important;
    border: 1px solid #B7C5D9 !important;
    border-radius: 3px !important;
    padding: 2px !important;
    /*Posts text font below*/
    color: #000000 !important;
}

}
