css:
@-moz-document domain(music.yandex.ru) {
.sidebar__under {
    display: none;
}
.page-root {
    background: #111 !important;
}
body {
    background: #111 !important;
}
.head {
    background: #111 !important;
    color: rgb(141, 134, 182) !important;
}
.head a {
    color: rgb(141, 134, 182) !important;
    text-decoration: underline;
}

span.nav__tab.nav__tab_switch {
text-decoration: underline !important;
}

a.button.button_size_L.head__user-button.button_ico.button_checked {
    background: rgb(141, 134, 182) !important;
    border-color: rgb(141, 134, 182) !important;
    color: white !important;
}

.nav__tab:active, .nav__tab:hover {
color: #fff !important;
text-decoration: none;
}

a.button.button_size_L.head__user-button.button_ico.button_checked:hover {
color: rgb(100, 40, 134) !important;
}

.sidebar {
    background: #111 !important;
}
.player-controls.player-controls_empty {
    background: #222 !important;
    color: #ccc;
}
.player-controls.player-controls_empty a {
    color: #fff !important;
}
}
