css:
@-moz-document domain("rubyfiddle.com") {
.cm-s-vibrant-ink {
  font-weight: bold!important;
}
.cm-s-vibrant-ink .CodeMirror-gutters {
  background: #000!important;
  border-right: 1px solid #888!important;
}
.cm-s-vibrant-ink .CodeMirror-linenumber {
  color: #8DA6CE!important;
}
.cm-s-vibrant-ink .cm-tag {
  color: #FFC66A!important;
}
.cm-s-vibrant-ink .cm-def {
  color: #fff!important;
}
.cm-s-vibrant-ink .cm-number {
  color: #8DA6CE!important;
}
.cm-s-vibrant-ink .cm-operator {
  color: #fff!important;
}
.row.full-width {
  background-color: #2D2D30!important;
}
h4 {
  color: #888!important;
}
footer {
  background-color: #2D2D30!important;
  color: #888!important;
}
body {
  background-color: #2D2D30!important;
}
.row {
  border-top: initial!important;
}
.panel {
  background: #2D2D30!important;
  color: #888!important;
  border-width: 0px!important;
}
p {
  color: #888!important;
}
hr {
  border-top-width: 0px!important;
}
.icon-bar.three-up {
  background: #2D2D30!important;
}
}
