css:
@-moz-document url-prefix('https://ello.co/') {

* {
	font-family: 'Roboto', sans-serif;
}

h1, h2, h3, h4, h5, h6, strong, b, th {
font-family: 'Roboto', sans-serif;
font-weight: 900;
}

i {
font-family: 'Roboto', sans-serif;
font-weight: 300;
}

[contenteditable="true"] b, [contenteditable="true"] b i, [contenteditable="true"] i b {
font-weight: 900;
}


strong em, em strong, b i, i b, b em, em b, strong i, i strong {
font-family: 'Roboto', sans-serif;
font-style: italic;
font-weight: 900;
}

  #toolbar {
    background-color: #808080 !important;
      border-right: 1px solid #000;
  }
#drawer {
  background-color: #808080 !important;
  border-right: 1px solid #000;
  
}
  .btn--tab {
    border-radius: 10px;
    float: none;
    
  }
  #featurebar {
    background-color: #101010;
    color: #000;
  }
  #featurebar a {
    color: #fff;
  }
  #featurebar span {
    color: #fff;
    
  }
  #omnibar_editor {
    border-radius: 10px;
  }
  .comment__editor {
    border-radius: 10px !important;
  }
  .js-comment-textarea {
    border-radius: 10px;
  }
  .post {
    border-top: 1px dashed #c0c0c0;
  }
  .post__avatar {
    top: 10px;
  }
  .post-content {
    padding-top: 5px;
  }
}
