css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://bato.to/read/") {


  body{
    background-image:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGBAMAAAAS4vJ7AAAAG1BMVEUAAAAbGxvd7h7d7h7d7h7d7h4bGxsbGxsbGxvf7wGmAAAAGklEQVQImWNIYGhnKGPgYGhPYGDgKGNggDIANMIEMGRbi90AAAAASUVORK5CYII=")!important;
  }
  
  #mainContent{
    background:none;
  }
  
  #mainContent,
  #content{
    padding:0!important;
  }
  
  #branding,
  #user_bar,
  #comic_wrap > div:nth-child(3),
  #content > div:nth-child(5),
  #footer_utilities,
  #stats_div,
  #full_image > div:nth-child(1),
  #zoom_notice{
    display:none;
  }
  
  #ipbwrapper{
    width:auto!important;
    max-width:100%!important;
  }
  
  #comic_wrap > div:nth-child(2){
    position:static!important;
    margin-bottom:0!important;
  }
  
  #comic_page{
    width:auto!important;
    margin:7px 0 0!important;
    border:none!important;
  }


}
