css:
@-moz-document domain("grupia.com") {
/* Bloco principal*/
.main-content-container{
  position: absolute;
  top: 4rem;
  padding-top: 2rem;
  bottom: 0;
  left: 0;
  right: 0;
  overflow-y: overlay;
  /*background-color: #E9E9E9;*/
  background: url(http://i.imgur.com/qUtAQYa.jpg) fixed #FFF !important; /* Fundo Site - Na ordem: Imagem de fundo / Caracteristica: Imagem fixa / Cor do fundo Caso o link não funcione / Não Retire o "!important"*/
}
/* Foto da conta - Cabeçalho*/
.header-account-info {
  position: absolute;
  right: -5rem;
  line-height: 0;
}

/* Menus Laterais*/
.side-menu ul {
  list-style-type: none;
  margin: 0;
  /*background: #E9E9E9;*/
    -webkit-border-radius: 2px;
	-moz-border-radius: 2px;
	border-radius: 2px;
  /*padding:1px*/
}
/* Menu Lateral -dir - Categorias*/
.side-menu.forum-categories .tag{
	-webkit-border-radius: 1px;
	-moz-border-radius: 1px;
	border-radius: 1px;
}
/* Caixa do Meio*/
.infinite-scroll-container{
	height:auto;
    width:110%
}
/* Menu Lateral - Direita*/
.secondary-sidebar{
  position:absolute;
  width: 17rem;
  right: -5rem;
}
  
/*Menus Laterais - Explorar comunidades*/

/* Menu Lateral Direito - Aviso "Suas Comunidades" */
.soft-card {
  /* background: #d2d2d2; */
  border-radius: 4px;
  padding: 1rem;
  margin-bottom: 1rem;
  color: #787878;
}
  
/* Caixa de resposta*/
.desktop-create-post-container,
.forum-fake-input-container {
  width: 110%;
  position: absolute;
}
/* Caixa de resposta - flutuante*/
.desktop-create-post-container.sticky .forum-create-post {
  position: absolute;
  bottom: 0;
  width: 68.5%;
}

/*Contador de páginas*/
.using-js .pagination-container {
  border-bottom: 0;
  box-shadow: none;
  height: 22px;
  width: 67.92%;
}
/*Contador de páginas - flutuante*/
.sticky .pagination-container {
  border-bottom: 1px solid #b4b4b4;
  box-shadow: 0px 1px 0px 0px rgba(0, 0, 0, 0.1);
  height: 2rem;
  width: 67.92%;
}
/* Bloco texto - Configurações*/
.user-profile-dashboard .info-block {
  margin-bottom: 3rem;
  background: #FFFFFF;
  padding: 5px;
}
}
