css:
@-moz-document url-prefix("http://www.lotus7.club/forum"), url-prefix("http://www.lotus7.club/comment") {
.views-field-topic-icon {
  display: none;
}

.shadowwrap {
    width: 100%;
}     

#wrapper {
    font-size: 1.1em;
    background-color: #c9ebf2;
}

#forum td {
    font-size: 1.0em;
    border: 0 none;
    padding: 4px 0px 4px 10px;
    background-color: #c9ebf2;
}    

.one-sidebar.sidebar-second #main {
    margin-right: 20px;
    width: 90%;
}
    
.one-sidebar.sidebar-second #headmain-wrap, .front.one-sidebar #headmain-wrap {
    width: 100%;
}    

.one-sidebar.sidebar-second #main {
    margin-right: 20px;
    width: 80%;
}   

.view-forum-home .views-table td {
    font-size: 1.1em;
    padding: 0px 0px 0px 10px;
    vertical-align: middle !important;
}
    
.view-forum-home th, .view-forum-home td {
    padding: 5px 0 5px 5px;
    width: 50%;
}    

.forum-post {
    padding: 0px 0px 2px 0px;
}

.forum-post-info {
    padding: 0px 25px;
}

.forum-post-header {
    height: 40px;
}

.picture {
    display: none;
}

.forum-post-wrapper {
    padding: 10px 25px 5px 25px;
  	font-size: 1.1em;
}

.forum-post-footer {
    height: 1.9em;
  	padding: 0px 25px 0px 25px;
  	margin-bottom: 0.5em;
}
    
p {
    line-height: 1.5em;
    margin-bottom: 0.5em;
}    

.flag-outer-report-content, .flag-outer-report-comments {
    display: block;
    margin-bottom: 0;
    text-align: right;
}    

#sharemarks {
    display: none;
}    
    
.forum-table-topics span.topic-icon {
    display: block;
}

span.topic-icon {
    height: 24px;
    text-indent: -9999px;
    width: 24px;
}

span.topic-icon {
    background-position: center center;
    border: 1px solid #808284;
    padding: 0;
}
}
