css:
@-moz-document url-prefix(http://www.nintendoclub.it/chat), url-prefix(http://www.nintendoclub.it/forumz), url-prefix(https://www.nintendoclub.it/forumz){  
  html > body{
    background: #CACAFF url("http://www.nintendoclub.it/style/marioworld.png") 50% 100% repeat-x fixed !important;
    font: 12px "Verdana", sans-serif;
  }
  
  .rich_editor{
    background: white !important;
  }

  html{
    font: 12px "Verdana", sans-serif;
  }
  
  #navbar{
    display: none;
  }
  
  body{
    font: 12px "Verdana", sans-serif;
  }

  img.avatar{
    max-width: 125px;
    max-height: 125px;
  }

  .poster h4{
    font-size: 125%;
  }

  .pagesection, .reset li, tbody p{
    font-size: 11px;
  }
  
  img.bbc_img{
    margin: initial;
    border: none;
  }
}
