css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".+/owa/.*"), domain("office365.com") {
  @font-face {
    font-family: 'Segoe UI WPC';
    src: local('sans-serif');
  }

  @font-face {
    font-family: 'Segoe UI WPC Semilight';
    src: local('sans-serif');
    font-weight: 100;
  }

  @font-face {
    font-family: 'Segoe UI WPC Semibold';
    src: local('sans-serif');
    font-weight: bold;
  }
}
