css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikia.com") {
  .Chat>ul>li, .Chat .inline-alert {
    margin: 0px;
    padding-bottom: 2px;;
    padding-top: 2px;
  }
  .Chat .continued {
    margin-bottom: 0;
    top:0;
  }
  .Chat img.avatar {
    margin-top: 0;
    top: 8px !important;
    /* border-radius: 5px; */
  }
  .Chat>ul>li, .Chat img.avatar {
  }
  .Rail .public.wordmark { 
    padding-bottom: 10px;
  }
  .Rail .selected {
    background-image: none;
    background-color: #AAFFFF;
    box-shadow:none;
  }
  .WikiChatList li.User {
    padding-top: 0;
    padding-bottom: 5px;
  }
  .message textarea, header#ChatHeader, section#WikiaPage {
    border-radius: 0;
  }
  
  /* shrink join/part messages */
  .Chat .inline-alert {
    font-size: 11px;
    height: auto;
    line-height: normal;
    font-weight: normal;
  }
  .Chat .inline-alert:before {
    content: "<< ";
  }
  .Chat .inline-alert:after {
    content: " >>";
  }
}
