css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntuusers.de")
 {


  /* Blocksatz und Microsoft-Schrift entfernen */
  p {
    text-align: left !important;
    font-family: DejaVu Sans !important;
  }

  /* Die Mini-Schrift vergrÃ¶ÃŸern */
  td.post > div.text > p,
  td.post > div.text > blockquote > p,
  td.post > div.text > ul > li > p,
  td.post > div.text > ul > li > p > code,
  td.post > div.text > p > code {
    font-size: 1.2em !important;
  }

  /* Inline-Code in `Backticks` hervorheben */
  td.post > div.text > p > code,
  td.post > div.text > ul > li > p > code,
  td.post > div.text > blockquote > p > code {
    border: 1px solid #ababab !important;
    background-color: #dedede !important;
  }

  /* Hintergrund vom Avatar entfernen */
  img.avatar {
    border: none !important;
    background: none !important;
  }

  /* Ãœberlange Userangaben abschneiden */
  td.author {
    overflow:hidden !important;
    white-space: nowrap !important;
  }

}
