css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.yahoo.com")

{

/*
Singularity Utopia Style
5 July 2017
*/

.takeover

{
  display: none!important;
}    
    
.fresh-toolbar .btn
    
    {
        color: #A80000 !important;
    }
    
.button-theme-transparent
    
    {
        opacity: 0.1;
    }
    
html, body, html#Stencil.loaded.yui3-js-enabled, #Stencil
    
    {
        overflow-y: hidden !important;
        max-height: 100% !important;
        height: 100% !important;
        margin: 0px!important;
        padding: 0px!important;
    }    
    
.message-list-toolbar, .message-list-group.scroll-locked
    
    {
        margin-right: 20px !important;
    }
    
.undoreset p:last-child
    
    {
        margin-bottom: 21px;
    }
    
.msg-body, .msg-body .email-wrapped, #paneshell

    {    
        max-width: 98% !important;  
        display: inline-block;
        word-wrap: break-word;
    }
         
.messagepane, #shellinner 
    
    {
        min-width: 815px !important;
        display: inline-flex;
        word-wrap: break-word;
        margin-bottom: 3px !important;
    }    
    
#shellcontent #shellinner, #paneshell
    
    {
        margin-bottom: 1px !important;
    }      
    
#toolbar 
    
    {
        border-color: whitesmoke #B0B9CF #B0B9CF #B0B9CF  !important;
        border-width: 1px !important; 
        border-style: solid;
        border-radius: 0px 0px 4px 4px;       
        border-bottom: #B0B9CF 1px solid !important;
        box-shadow:  inset white 0px -1px 0px;
        background: whitesmoke !important;
    }
    
.anchored-header
    
    {
        display:none !important;
    }

.base-card
    
    {
        margin-top: -2px !important;
    }
         
.compose-message
    
    {
        word-wrap: break-word;        
    }
           
#yucs-top-bar, #Eyebrow
    
    {
        display:none;       
    }
    
#main 
    
    {
        top: 52px !important;
    }
    
#UH 
    
    {
        height: 0px !important;
    }
        
.StencilRoot.search-container
    
    {
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        padding-bottom: 0px!important;
    }
    
body, .StencilRoot
    
    {
        margin-top:11px !important;
        margin-bottom: 0px!important;
        padding-bottom: 0px!important;
    }
    
.tictac-att-files-cover
    
    {
        margin-bottom: 14px !important;
    }
    
#theAd, .list-view-item-date-label, .mb-list-ad.mb-native-ad, .ml-compact .mb-list-ad, .ad-tl2b, .mb-list-ad.mb-list-img-ad, .mb-rec-ad .rec-img, .mb-rec-ad .icon
       
    {
        display:none !important;
    }
    
#msg-list.with-right-rail #dd_search_background_helper_overlay
    
    {
        margin-right: 0px;     
    }

#msg-list.with-right-rail .list-view-items, #msg-list.with-right-rail .list-view-items
    
    {
        padding-right: 0px; 
    }
      
}    

/* 
The Singularity is Near
*/
