css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.waterstad.fm/player/") {
    #players
    {
       display: none;
    }
    
    *
    {
       overflow: hidden;
    }
    
    #logo
    {
        visibility: hidden;
        height: 0px;
    }
    
    .content2
    {
        padding-left: 0px;
        padding-right: 0px;
        margin-bottom: 0px;
    }
    
    #holder
    {
       background: none;
    }
}

@-moz-document url("http://www.waterstadfm.nl/wp-content/themes/waterstadfm2013/laatst_gespeeldwfm.php") {
    .icons
    {
       display: none;
    }
    
    
    .trackDescription
    {
        width: 77%;
    }
    
    #gespeeld
    {
       background: #000;
        margin-top: 0px;
        padding-top: 0px;
        height: 233px;
    }
    
    h2.gespeeld 
    {
        margin-top: 0px;
       /* padding-top: 0px;*/
    }
}
