css:
@-moz-document url-prefix("http://bato.to/read"){
      
    #branding,
    #user_bar,
    #comic_wrap > div:nth-child(3),
    #content > div:nth-child(5),
    #footer_utilities,
    #stats_div,
    #full_image > div:nth-child(1),
    #zoom_notice{
       display:none;
    }

    #mainContent,
    #content{
        padding:0!important;
        background:white;
    } 
    
    #content > div:nth-child(4){
        display:none;
    }
    
    #comic_wrap > div:nth-child(2){
       position:static!important;
       margin-bottom:0!important;
       right:0px!important;
    }

    .wrapper{
        max-width:100%;
        width:100%;     
    }
    
    #comic_page{
        min-width:100%;
    }
    
}
