css:
@-moz-document domain("www.iciba.com") {
/* hidden ad and useless element */
.head_ad,
.header_ad,
.vRecomm,
.pendant,
.search_foot,
#ezfm,
.header_sea .header_nav,
.baidu { display: none; }


.header { visibility: hidden;}
.header .headerC { visibility: visible; }

#dict_main, .dictbar, .dict_title { width: 100%; }

#mainbox { width: auto; }

#side_bar,
#footer { display:none;}

.bg_main { background: #eee; }

.dictbar,
.tab_authorities,
.dict_content,
.tab_specialties { 
  background: #fff;
  padding: 1em; 
  border-radius: 5px;

}
      
.dict_main,.dict_bar, .tab_list { width: auto;}
}
