css:
@-moz-document domain("weibo.com") {
/*号称屏幕阅读友好的微软雅黑*/

body {
font-family: 'Arial', sans-serif;
}



/*半透明*/

.WB_frame {
background-color: rgba(73,72,72,0.5)!important;
}



/*浪费空间的10像素*/

.WB_feed .WB_cardwrap {
margin-bottom: 0px!important;
border-bottom: 1px solid rgba(0, 0, 0, 0.2);
}

.WB_cardwrap {
box-shadow: none!important;
}



/*新微博提示*/

.WB_notes {
margin: 0 0 10px!important;
padding: 0!important;
}

.WB_cardwrap.WB_notes {
margin: 0 0 10px!important;
}



/*微博内部*/

.WB_feed_detail {
padding: 15px 16px 10px 10px;
}


/*NOOOOO圆角*/
.WB_cardwrap {
border-radius: 0px!important; 
}

.send_weibo {
border-radius: 0px!important; 
}

.WB_tab_a .tab li:nth-child(2) .t {
border-top-left-radius: 0px;
}

.WB_tab_a .tab li:first-child,.WB_tab_a .tab li:nth-child(2){ border-top-left-radius:0px; border-bottom-left-radius:0px;}
WB_tab_a .tab li:nth-child(2) .t{ border-top-left-radius:0px;}.WB_tab_a .tab li:nth-child(2) .b .b1 em.l i{ border-bottom-left-radius:0px;}

.WB_tab_a .tab_box .fr_box {
border-top-right-radius: 0px;
border-bottom-right-radius: 0px;
}



/*逗比的名片栏*/

.W_person_info {
margin: 0 !important;
}

.user_atten li {
width: 65px;
}



/*伤害强迫症的下一页*/

.W_pages .list {
width: 100px;
margin: 0;
}

.W_pages .next {
margin: 0;
width: 99px;
}


/*个人主页*/

.B_page .WB_frame {
width: 960px;
}

.WB_frame_a, .WB_frame_a_fix {
margin-right: 20px;
margin-left: 20px;
}

.B_page .WB_frame #plc_main {
width: 940px;
padding: 0 20px;
}

.WB_timeline {
margin-left: 480px;
}

.B_page a.W_gotop {
margin-left: 480px;
}



/*我的相册*/

.PCD_photoflow .piclist_wrap {
margin-left: -20px;
}


/*管理中心*/
.WB_frame_a_fix {
margin-left: 0;
}


}
