css:
@-moz-document url-prefix("http://weather.gc.ca/city/pages/"), url-prefix("https://weather.gc.ca/city/pages/") {
h1 {
  font-size:24px; 
}
.h2, h2 {
  font-size:16px;
  position:relative;
  top:3px;
}
body {
  font-size:15px; 
}
.wxo-tp-banner {
  display:none; 
}
#wxo-currentimg{
  margin-top:20px;
}
.dl-horizontal dt {
  text-align:left;
  padding-left:20px;
}
.lead {
  font-size:28px;          
}
dl.wxo-dl-cnd dd{
  margin-left:0px!important;
}
dl.wxo-dl dd {
  margin-left:0px!important; 
}
dl.wxo-dl-cnd dt{
  width:20%!important;
}
dl.wxo-dl dt{
  width:41%!important;
}
.wxo-cnd-lft{
  margin-top:10px;
}
.brdr-bttm{
  padding:5px 0 10px 0;
}
.pop{
  font-size:12px!important;
  margin:2px 0 2px 0;
}
.high{
  margin-bottom:5px;
}
.wxo-subLine{
  padding:5px 0 5px 0;          
}
.col-sm-4 .wxo-dl{
  margin-top:15px;
  position:relative;
  left:70px;
}
}
