@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("autoit.de") {
  div > a.framed > img, /*Posts*/
  div > p.framed > img, /*Threadübersicht*/
  div > p.framed > a.framed > img, /*Mitgliederliste*/
  div > small.framed > img, /*Beteiligung Threadübersicht*/
  div > span.framed > img, /*Profil Kommentar antworten*/
  li > a.framed > img, /*Header-Leiste*/
  li > a > div.framed > img, /*Header-Leiste aufgeklappt*/
  li > span.framed > img, /*Profil Kommentar schreiben*/
  div.userProfilePreview > a > img /*Popup-Box bei Hover von Username*/
  {
     -webkit-filter: grayscale(100%);
     filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.5 0.5 0.5 0 0 0 0 0 1 0\'/></filter></svg>#grayscale");
  }
}
