@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("beta.hive.im") {
/* #welcome {background-image: url('http://i.imgur.com/STBmIZh.png') !important;} */
/* #welcome {background-image: url('http://i.imgur.com/7low4ZR.png') !important;} */
  #welcome {background-image: url('http://i.imgur.com/BlEUcby.png') !important;}
  #find_files, #find_friends {border-left: 1px solid #1E1E1E !important;}
  #download_file {background-color: #00904B !important;}
  #comments_advert, #sparkle, .fb-like, #member_advert {display :none !important;}
  #summary, #tree,  #root #primary {width: 413px !important;}
  #summary {height: 360px !important;}
  #tree {top: 360px !important;}
  #add {bottom: 14px !important;}
  #tree > div > ul {width: 400px !important;}
  #tree li > p {height: 18px !important; text-transform: none !important; line-height: 20px !important; background-position: left center !important;}
  #content {left: 400px !important; width: 53% !important;}
  .scroll_width {width: 100% !important;}
  #grid {width: 90% !important; border-left: 50px solid transparent !important; border-right: 50px solid transparent !important;}
  #order {width: 80% !important;}
  #grid.list > li, #drag.list > li, #grid.list > li *, #drag.list > li * {text-transform: none !important; height: 28px !important; }
  #grid.list .icon, #drag.list .icon {width: 26px !important; height: 26px !important;}
  #grid.list .label, #drag.list .label {padding: 0px 4px !important; height: 26px !important; margin: -6px 0px 0px 40px !important;}
  #grid.list .size, #drag.list .size {margin: -6px 0px 0px 40px !important;}
  .label, #uploads_list > li > p, #transfers_list > li > p {text-transform: none !important;}
  .no_pending > p, .no_transfers > p, [data-id="clear"] > p {color: #FFFFFF !important; background: #949494 !important; border-radius: 0px 20px 20px 0px !important;}
  #friends_list {width: 16%  !important;}
  #friends {width: 70%  !important;}
  #add_screen, #friends_screen, #account_screen, #premium_screen, #settings_screen, #help_screen, #about_screen, #careers_screen, #privacy_screen, #copyright_screen, #terms_screen {width: 91%  !important;}
  #premium_upgrade {width: 75%  !important;}
  #activity_screen, #legal  {width: 90% !important;}
  #drop, #dropfiles, #retrieve, #retrieve > div > textarea {width: 120px !important; height: 120px !important;}
  #import {margin: 0px 0px 0px 87px !important; width: 120px !important; height: 120px !important;}
  #retrieve {margin: 0px 60px 0px 503px !important;}
  #add_screen > div {height: 140px !important;}
  #add_screen > h1:nth-child(2) {left: 277px !important;}
  #add_screen > h1:nth-child(3) {left: 501px !important;}
  #friends_list ul > li:first-child {padding: 15px 0px 0px 5px !important;}
  div.activity_footer > p {font-size: 10px !important; color: #1E1E1E !important; width: 65% !important;}}
