css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

#oh-menu-upgrade > a {
  display: none;
}

body.deviantart #overhead .oh-gap + td:not(#oh-menu-readonly) {
    border-left: none;
}

#oh-menu-deviant, #oh-menu-split, #oh-menu-friends, #oh-menu-collect, #oh-menu-cart, #oh-menu-join, #oh-loginbutton, #overhead .oh-mc-split, #oh-menu-greeting {
    padding: 0px;
    background-image: linear-gradient(180deg, #5C6D5B, #6F836E);
}
  
body #overhead {
    background-color: #667865;
    background-image: linear-gradient(#5C6D5B, #6F836E);
}
}
