css:
@-moz-document url-prefix("https://freedcamp.com") {
	body{
		background-color : #444 !important;
		color : #CCC !important;
		border-color : #555 !important
	}
	.fc-header-wrap, .fc-header-wrap header.fc-header .fc-user-actions ul.app{
		background-color : #194664 !important;
		border-color : #123349 !important;
	}
		.fc-header-wrap header.fc-header .fc-navigation .fc-project-nav .fc-current a.fc-choose{
		border-color : #123349 !important;
	}
	/** Buttons **/
	.fc-header-wrap header.fc-header .fc-user-actions ul.app li a, .fc-header-wrap header.fc-header .fc-user-actions .user, .fc-header-wrap header.fc-header .fc-navigation a.dashboard{
		background-color : #1b4c6d !important;
		border-color : #123349 !important;
	}


	.application_bar{
		background-color : #333 !important;
		border-color : #222 !important;
	}
	.action_bar{
		background-color : #333 !important;
	}
	.grey_txt{
		color : #CCC !important;
	}
	ul li.widget, li.widget div.body ul li{
		border-color : #333 !important;
	}
}
