css:
/* TOKYOTOSHO REVISED */
@-moz-document domain("tokyotosho.com"), domain("tokyotosho.info") 

{

body {
font-family: Candara, Sans !important;
font-size: 8pt !important;
color: #C0C0C0 !important;
}

a {
font-family: Segoe UI, Sans !important;
font-size: 8pt !important;
text-decoration: none !important;
}
.irc:link {color: #487524 !important;}
.irc:hover {color: #66A530 !important; text-decoration: overline !important;}
a:link {color: #FF8A3D !important;}
a:visited {color: #FFD9C1 !important; font-style: italic !important; text-decoration: none !important;}
a:hover {color: #0094FF !important; text-decoration: overline !important;}
.shade {
background-color: #273A49 !important;
}
#main {
background-color: #283138 !important;
}

}
