css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
  .pagetop {
    font-size:2em !important;
    line-height: 1.5em;
  }
  body > center:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    vertical-align: top;
    padding-top: 10px;
  }
  .pagetop img {
    display: block;
  }
  table {
    width: 100%;
  }
  table table table tr:first-child td:first-child { width: 0;}
  .title{
    font-size: 2.5em;
  }
  .title span {
    font-size: 1.5em;
  }
  .subtext {
    font-size: 2em;
  }
  tr[style] {
    height: 10pt !important;
  }
  font[size] {
    font-size: 1em !important;
  }
  span.comment {
    font-size: 1.2em !important;
  }
  .comhead {
    font-size: 0.9em !important;
  }
  img[width="40"] {
    width: 20px !important;
  }
  img[width="80"] {
    width: 40px !important;
  }
  img[width="120"] {
    width: 60px !important;
  }
}
