css:
@-moz-document domain("wikipedia.org") {

#mw-head-base, #mw-navigation, #mw-page-base, #footer-info-copyright, #footer-icons, #footer-places {
    display: none;
}

/* Can't display:none the "from wikibedia" subheader because a page's coordinates get pushed up into the topmost divider */
#siteSub {
    color: transparent;
}

/* remove menu sidebar artifact and footer spacing */
#content, #footer {
    margin: 0 !important;
}

/* minor 1px blu eborder artifact on the top and the left from side/top bars */
.mw-body {
    border: none;
}

}
