css:
@-moz-document domain('feedly.com'){
  .shareBarHolder,
  a.fx-button,
  .tagsHolder {
    display: none !important;
  }
  .inlineFrame,
  .inlineFrame.selected {
    margin: 0;
    border-left: 0 !important;
    border-right: 0 !important;
    border-radius: 0 !important;
  }
  img.pinable {
    margin: 0 !important;
  }
  .entryholder {
    padding: 0;
  }
  #feedlyPageFX,
  .u100Entry,
  .entryBody {
    max-width: none;
  }
}
