css:
@-moz-document domain("bf-blog.cz") {
#page, body > footer {
	width: 75%!important;
}
#page .content, footer .container, .small_film {
  	background-color: #050505 !important;
}
.primary-navigation, .tooltip-content, .tooltip-content .widget, .sub-menu, .tooltip-content2, #az-listing section ol ul, #azmenu, #az-listing section ol, #navigation ul ul li {
  	background-color:#111 !important;
    box-shadow: 1px 2px 1px #000 !important;
}
.nav-center {
 	border: none !important;
}
.featured-thumbnail {
	width: 40% !important;
    float: right !important;
}
.home-content {
  	clear: right !important;
    width: auto !important;
}
.post.excerpt {
 	background-color:#111 !important;
    border: none !important;
}
.post.excerpt .featured-thumbnail img {
    min-height: 0 !important;
}
.sidebar.c-4-12 {
    width: 29.2% !important;
}
.article{
    width: 70% !important;
}
.post-info-home {
	margin: 0 !important;
    padding: 0 !important;
}
#shoutbox #input_area table {
 	border: none !important;
}
input#sb_addmessage {
 	background-color: #dd9933 !important;
  	color: white !important;
    border-color: #dd9933 !important;
}
input#sb_addmessage:hover {
 	background-color: #eeaa44!important;
  	cursor: pointer !important;
}
#header .image-logo img {
  	display: none !important;
  	content: "BF-blog.cz" !important;
}
.primary-nav nav {
    padding: 10px 0 0 0 !important;
}
.secondary-navigation {
    padding-left: 20% !important;
}
}
