/* 
// Tahoma font and better RTL support for Feedspot.com
// By Salar Khalilzadeh
// http://blog.salarcode.com
*/

@-moz-document domain("feedspot.com") {
.rtl,.ocn,.omsds1 span, .omsds11 span {
	font-family: tahoma !important; 
}
.rtl *{
	font-family: tahoma !important; 
}
.rtl .ocn-data-desc,.rtl  .ocn-data-desc ol,.rtl  .ocn-data-desc ul,.rtl  .ocn-data-desc li,.rtl  .ocn-data-desc p,.rtl  .ocn-data-desc td{
	font-family: tahoma !important; 
}

.rtl .fav_gh_setting{
	margin-left:0;
}
.ext_link{
	font-family: 'b yekan' !important; 
}

.rtl .ocn-data-desc {
	text-align:right !important;
}
.ocn-data-desc p{
	text-align:justify;
}
.jspContainer{
	font-family: tahoma;
}

.rtl .omsds2 .so-lh{
	right:0px;
	
}
.rtl .top-sb ul{
	padding: 0;
}
}
