css:
@-moz-document domain(lemonde.fr) {
@media print {
  body {    
    width: 100%;    
    height: 100%;    
    margin: 0;    
    padding: 0;
  }
  article {    
    position: static;    
    top: 0;    
    left: 0;    
    width: 100% !important;    
    height: 100% !important;
  }
  article .toolbar, article .fb-like {    
    display: none !important;
  }
  #body-publicite, .global, .container_18, .grid_10, .grid_12 {    
    padding: 0 !important;    
    margin: 0 !important;    
    position: static !important;    
    display: block !important;    
    height: 100% !important;    
    width: 100% !important;
  }
  .banniere {    
    display: none !important;
  }
}

#bandeau_bas, #bandeau_bas * {
    display: none;
}
}
