css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.live.com") {
  
  li.c-MessageRow.mlRd.mlUnsel:not(.mlUnrd) {
    background-color: rgba(230, 230, 230, 0.85);
  }
  
  li.c-MessageRow.mlRd.t_sbgc .t_s_hov:hover, 
  li.c-MessageRow.mlRd.t_s_hov:hover, 
  li.c-MessageRow.mlRd.t_s_hov:hover .t_s_hovh, 
  li.c-MessageRow.mlRd.t_s_sel, 
  li.c-MessageRow.mlRd.t_s_h {
    background-color: rgba(230, 230, 230, 0.85);
  }
  

  .mlRd .Dt,
  .mlRd .FmSender,
  .mlRd .t_estc
  {
    color: rgb(100, 100, 100);
  }
  
  
  li.c-MessageRow.mlUnrd.t_sbgc .t_s_hov:hover, 
  li.c-MessageRow.mlUnrd.t_s_hov:hover, 
  li.c-MessageRow.mlUnrd.t_s_hov:hover .t_s_hovh, 
  li.c-MessageRow.mlUnrd.t_s_sel, 
  li.c-MessageRow.mlUnrd.t_s_h {
    background-color: rgba(255, 255, 255, 0.99);
  }

  
  li.leftnavitem .Unread{
    color: #0072c6;
    font-size: 15px;
    font-weight: bold;
  }
  
  
  .Unread .count {
    border: 1px solid;
    border-color: #0072c6;
    padding: 2px 6px 2px 6px;
    border-radius: 8px;
    color: white;
    background-color: #0072c6;
    
  }
}
