css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') {
  
#new_post_notice_container{ background-color: #ff0000; color: #ffffff; }
.popover_blogs .tab_notice{ color: #ffffff; }
.identity-refresh .tab_notice{ color: #ffffff; }
.identity-refresh .selected .tab_notice{ color: #ffffff;}
.identity-refresh .tab_notice .tab_notice_value{ color: #ffffff; }
.identity-refresh .inbox_notice_container,.identity-refresh .new_post_notice_container{ color: #ffffff;}
.identity-refresh .inbox_notice_container,.identity-refresh .new_post_notice_container{
    background-color: #ff0000;} 
  
  
}
