css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.post.post_full {
width: 500px!Important;}

.post_full.is_full_width.is_photo .post_media_photo {
height: auto!important;}

.post_full .post_media {
position: relative;
}
.post_full.is_photoset .photoset .photoset_row .photoset_photo img {
width: auto!important;
}
.photoset_row {
width: 500px!important;
margin-left: 0px!important;
}

.post_full.is_full_width.is_photo .post_media_photo {height: auto!important; max-width: 500px!important;}

.post_full.is_photoset .photoset .photoset_row .photoset_photo img {width: auto!important;}
.photoset_row {width: 500px!important;}
.photoset_row_1 img {max-width: 500px!Important;}
.photoset_row_2 img, .photoset_row.photoset_row_2 img, .photoset_row.photoset_row_2>a>img, .photoset_row.photoset_row_2>img {max-width: 245px!important;}
.photoset_row_3 img, .photoset_row.photoset_row_3 img, .photoset_row.photoset_row_3>a>img, .photoset_row.photoset_row_3>img {max-width: 160px!important;}
.post_full.is_full_width.is_photoset .photoset .photoset_row .photoset_photo {margin-left: 10px;}
.post_full.is_full_width.is_photoset .photoset .photoset_row, .post_full.is_full_width.is_photo .photoset .photoset_row {margin-top: 10px;}

.photoset_row.photoset_row_1 {margin-left: 0px!Important;}

.dockable_video_embed {
width: 500px!important;
}

.embed_iframe {
width: 500px!important;
}

.post.new_post .new_post_label {
width: 70px;}

#posts.posts {
margin: 0 0 0 105px;}

.l-container.l-container--two-column .left_column, .l-container.l-container--two-column-dashboard .left_column {
width: 605px;
}

.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
width: 249px;
}
.post_full.is_note .post_body .note_item, .post-composer_note-post .note_item {
width: 404px;}

.post_full.is_video .crt-video {
max-width: 500px!important;
}

.photoset_row.photoset_row_1>a>img {
width: 500px!important;
min-width: 500px!important;
}
.post.new_post .post_buttons {
padding: 0;
}


#post_controls {
margin: 20px 0px 0;
}

.ui_tops .tops_box.tops_post {
padding-left: 10px!important;}


.notification {
width: 500px;}

.notification .notification_sentence {
max-width: 440px;}

.post .tags {
width: 460px!important;}


.tag_editor {
margin: 0;
}

input.full, textarea.full {
width: 458px;}



#new_post #post_content {
padding: 10px 0px 18px;
}
#new_post #post_content>div {
margin: 0 20px;
}
#post_controls {
margin: 20px 20px 0;
}
.main_content.photo_form {
margin: 0px!Important;
}
.main_content.photo_form .inner_content {
margin: 0px 20px;
}
form[data-post-type="photo"] .tag_editor {
margin: 0 20px;
}
.main_content.photo_form .tags {
width: 460px!Important;
}
.post_full.is_photoset .photoset .photoset_row .photoset_photo {margin-left:10px!important;}
.post_full.is_photoset .photoset .photoset_row:first-child, .post_full.is_photo .photoset .photoset_row:first-child {margin-top: 0!important;}
.post_full.is_photoset .photoset .photoset_row, .post_full.is_photo .photoset .photoset_row {margin-top: 10px!important;}
.video_preview{
max-width: 460px;
}
.post_full .post_body .inline_image {max-width: 100% !important; height: auto !important;}
.post .post_content img {height: auto!important;}

.photoset_row {
height: inherit!important;
}
}
