css:
/* MiaP97 - userstyles.org */
/* Required for some reason */
@namespace url(http://www.w3.org/1999/xhtml);

/* Makes sure rules only apply to tumblr dashboard */
@-moz-document url(http://tumblr.com/dashboard), url(https://tumblr.com/dashboard), url(http://www.tumblr.com/dashboard), url(https://www.tumblr.com/dashboard) {

/* Moves arrow to the top (as opposed to the bottom) */
.elevator-wrapper {
  top: 15px !important;
  right: 15px !important;
}

/* Fixes the sizing */
.elevator {
    transition: opacity 150ms ease-out 0s, transform 150ms ease-out 0s;
    color: rgba(255, 255, 255, 0.5);
    pointer-events: none;
    text-align: center;
    line-height: 32px;
    cursor: pointer;
    font-size: 50px;
    display: block;
    height: 32px;
    width: 32px;
    transform: translateY(15px) translateZ(0px);
    opacity: 0;
}
}
